import ACTIONS from "../Constants/ACTIONS";
import { nanoid } from "nanoid";
const reducer = (state, action) => {
  switch (action.type) {
    case ACTIONS.KEY_PRESS:
      //
      // put currentWord by index of words arr
      //
      const newChar = {
        ...state.currentChar,
        id: nanoid(),
        text: action.payload.toUpperCase(),
      };
      const currentWord = [...state.currentWord, newChar];
      const wordsArr = [...state.wordsArr];
      //
      // console.log(indexOfFirstEmptyArrInWordsArr);
      wordsArr.splice(state.currentRow, 1, [...currentWord]);
      if (state.currentWord.length < 5) {
        return {
          ...state,
          currentChar: newChar,
          currentWord: currentWord,
          // wordsArr: wordsArr,
        };
      } else {
        return {
          ...state,
          currentChar: { ...state.currentChar, text: action.payload },
        };
      }
    case ACTIONS.DELETE_CHAR:
      return {
        ...state,
        currentWord: state.currentWord.slice(0, -1),
      };
    case ACTIONS.CONFIRM_WORD:
      //////////////
      let message;
      if (state.currentWord.length === 5 && state.wordsArr.length <= 6) {
        const newCurrentWord = state.currentWord.map((char, i) => {
          let status;
          for (let k = 0; k < state.mysteryWord.length; k++) {
            if (char.text === state.mysteryWord[k] && i === k) {
              status = "correct-spot";
              break;
            } else if (char.text === state.mysteryWord[k] && i !== k) {
              status = "wrong-spot";
              // break;
            }
          }
          status = !status ? "wrong-char" : status;
          return {
            ...char,
            status: status,
          };
        });
        if (newCurrentWord.every((char) => char.status === "correct-spot")) {
          console.log(123);
          message = "Good job!";
        }
        // console.log();
        ////////////////
        const indexOfWordsArr = state.wordsArr.findIndex(
          (arr) => arr[0].id === ""
        );
        const newWordsArr = [...state.wordsArr];
        newWordsArr.splice(+indexOfWordsArr, 1, newCurrentWord);
        ////////////////
        return {
          ...state,
          wordsArr: newWordsArr,
          currentWord: [],
          message: message,
          currentRow: state.currentRow + 1,
        };
      } else {
        return {
          ...state,
          errorMessage: "Not enough letters",
        };
      }
    default:
      return state;
  }
};
export default reducer;
