import ChainOfCells from "./ChainOfCells";
import { nanoid } from "nanoid";

export default function Board({ data }) {
  // const wordsElements = data.wordsArr.map((word) => (
  //   <ChainOfCells key={nanoid()} word={word} />
  // ));
  const wordsElements = wordsArr.map((word) => (
    <ChainOfCells key={nanoid()} word={word} />
  ));
  return (
    <div className="board">
      {data.wordsArr.length > 0 && wordsElements}
      {data.wordsArr.length < 6 && (
        <ChainOfCells key={nanoid()} word={data.currentWord} />
      )}
    </div>
  );
}
