import React, { useEffect } from "react";
import Board from "./Components/Board";
import "./index.css";
import WORDS from "./Constants/WORDS.js";
import ACTIONS from "./Constants/ACTIONS.js";
import { nanoid } from "nanoid";

function containsSpecialChars(str) {
  const specialChars = /[`!@#$%^&*()_+\-=\[\]{};':"\\|,.<>\/?~]/;
  return specialChars.test(str);
}
const randomNumber = Math.floor(Math.random() * (WORDS.length + 1));

function reducer(state, action) {
  switch (action.type) {
    case ACTIONS.KEY_PRESS:
      // const newChar = action.payload.toUpperCase();
      const newChar = {
        ...state.currentChar,
        id: nanoid(),
        text: action.payload.toUpperCase(),
      };
      if (state.currentWord.length < 5) {
        return {
          ...state,
          currentChar: newChar,
          currentWord: [...state.currentWord, newChar],
        };
      } else {
        return {
          ...state,
          currentChar: { ...state.currentChar, text: action.payload },
        };
      }
    case ACTIONS.DELETE_CHAR:
      return {
        ...state,
        currentWord: state.currentWord.slice(0, -1),
      };
    case ACTIONS.CONFIRM_WORD:
      //////////////
      if (state.currentWord.length === 5) {
        const newCurrentWord = state.currentWord.map((char, i) => {
          let status;
          for (let k = 0; k < state.mysteryWord.length; k++) {
            if (char.text !== state.mysteryWord[k] && i !== k) {
              status = "wrong-char";
            } else if (char.text === state.mysteryWord[k] && i !== k) {
              status = "wrong-spot";
              break;
            } else if (char.text === state.mysteryWord[k] && i === k) {
              status = "correct-spot";
              break;
            }
          }
          return {
            ...char,
            status: status,
          };
        });
        if (newCurrentWord.every((char) => char.status === "correct-spot")) {
          console.log(123);
        }
        // console.log();
        ////////////////
        return {
          ...state,
          wordsArr: [...state.wordsArr, newCurrentWord],
          currentWord: [],
        };
      } else {
        return {
          ...state,
          errorMessage: "Not enough letters",
        };
      }
    default:
      return state;
  }
}
export default function App() {
  const [state, dispatch] = React.useReducer(reducer, {
    mysteryWord: WORDS[randomNumber].toUpperCase().split(""),
    wordsArr: [],
    currentChar: {
      id: nanoid(),
      status: "",
      text: "",
    },
    currentWord: [],
    errorMessage: "",
  });
  useEffect(
    () =>
      document.addEventListener("keydown", (e) => {
        const pressedKey = e.key.toUpperCase();
        console.log(pressedKey);
        if (pressedKey === "BACKSPACE") {
          dispatch({ type: ACTIONS.DELETE_CHAR });
        } else if (pressedKey === "ENTER") {
          dispatch({ type: ACTIONS.CONFIRM_WORD });
        } else if (
          pressedKey.length === 1 &&
          !isFinite(pressedKey) &&
          !containsSpecialChars(pressedKey)
        ) {
          dispatch({ type: ACTIONS.KEY_PRESS, payload: e.key });
        } else {
          return;
        }
      }),
    []
  );
  console.log(state);
  console.log(WORDS);
  return (
    <div className="App">
      <div className="header">
        <h1>Wordle</h1>
      </div>
      <div className="game">
        <Board data={state} />
      </div>
    </div>
  );
}
