import React, { useEffect, useReducer } from "react";
import { nanoid } from "nanoid";
import "./index.css";
import WORDS from "./Constants/WORDS.js";
import ACTIONS from "./Constants/ACTIONS.js";
import SPECIAL_CHARS from "./Constants/SPECIAL_CHARS";
import generateRandomNumber from "./Functions/generateRandomNumber";
import generateMysteryWord from "./Functions/generateMysteryWord";
import checkForSpecialChars from "./Functions/checkForSpecialChars";
import reducer from "./Functions/reducer";
import Board from "./Components/Board";

export default function App() {
  const [state, dispatch] = useReducer(reducer, {
    mysteryWord: gene,
    wordsArr: [],
    currentChar: {
      id: nanoid(),
      status: "",
      text: "",
    },
    currentWord: [],
    errorMessage: "",
  });
  useEffect(
    () =>
      document.addEventListener("keydown", (e) => {
        const pressedKey = e.key.toUpperCase();
        console.log(pressedKey);
        if (pressedKey === "BACKSPACE") {
          dispatch({ type: ACTIONS.DELETE_CHAR });
        } else if (pressedKey === "ENTER") {
          dispatch({ type: ACTIONS.CONFIRM_WORD });
        } else if (
          pressedKey.length === 1 &&
          !isFinite(pressedKey) &&
          !checkForSpecialChars(pressedKey, SPECIAL_CHARS)
        ) {
          dispatch({ type: ACTIONS.KEY_PRESS, payload: e.key });
        } else {
          return;
        }
      }),
    []
  );
