import React, { useEffect, useReducer } from "react";
import { nanoid } from "nanoid";
import "./index.css";
import WORDS from "./Constants/WORDS.js";
import generateMysteryWord from "./Functions/generateMysteryWord";
import reducer from "./Functions/reducer";
import Board from "./Components/Board";
import handleKeyPress from "./Functions/handleKeyPress";
import ChainOfCells from "./Components/ChainOfCells";

const cellArr = [1, 2, 3, 4, 5, 6];
const wordArr = [
  { text: " 1", status: " ", id: nanoid() },
  { text: " 2", status: " ", id: nanoid() },
  { text: " 3", status: " ", id: nanoid() },
  { text: " 4", status: " ", id: nanoid() },
  { text: " 5", status: " ", id: nanoid() },
];
const solution = generateMysteryWord(WORDS);
export default function App() {
  const [state, dispatch] = useReducer(reducer, {
    mysteryWord: solution,
    wordsArr: [],
    currentChar: {
      id: nanoid(),
      status: "",
      text: "",
    },
    currentWord: [],
    errorMessage: "",
    message: "",
  });
  useEffect(
    () =>
      document.addEventListener("keydown", (e) => handleKeyPress(e, dispatch)),
    []
  );
  console.log(state);
  console.log(state.mysteryWord);
  const chainOfCellsElements = cellArr.forEach((el) => (
    <ChainOfCells key={el} word={wordArr} />
  ));
  return (
    <div className="App">
      <div className="header">
        <h1>Wordle</h1>
      </div>
      <div>{chainOfCellsElements}</div>
      {/* {state.errorMessage && <div>{state.errorMessage}</div>} */}
      <div className="game">
        <Board data={state} />
      </div>
    </div>
  );
}
